mapper.select:

select 

from sys_menu sm
where id = #{id}

select DISTINCT (sm.id),
from sys_menu sm left join sys_role_menu srm on sm.id=srm.menu_id left join sys_role sr on sr.id=srm.role_id left join sys_account_role sar on sar.role_id=sr.id where sm.parent_id=0
and sar.account_id=#{id}

select DISTINCT (sm.id),
from sys_menu sm left join sys_role_menu srm on sm.id=srm.menu_id left join sys_role sr on sr.id=srm.role_id left join sys_account_role sar on sar.role_id=sr.id where sm.parent_id=#{parentId}
and sar.account_id=#{accountId}
