mapper.select:



		AND menu_name like concat('%', #{menuName}, '%')


		AND visible = #{visible}


		AND status = #{status}


order by parent_id, order_num

		select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.`query`, m.visible, m.status, ifnull(m.perms,'') as perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time
		from sys_menu m
		left join sys_role_menu rm on m.menu_id = rm.menu_id
		left join sys_account_role ur on rm.role_id = ur.role_id
		left join sys_role ro on ur.role_id = ro.id
		where ur.account_id = #{params.userId}

            AND m.menu_name like concat('%', #{menuName}, '%')


            AND m.visible = #{visible}


            AND m.status = #{status}

		order by m.parent_id, m.order_num

select m.menu_id from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, `query` = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}

mapper.select:
select DISTINCT (sm.menu_id),
from sys_menu sm left join sys_role_menu srm on sm.menu_id=srm.menu_id left join sys_role sr on sr.id=srm.role_id left join sys_account_role sar on sar.role_id=sr.id where sm.parent_id=#{parentId}
and sar.account_id=#{accountId}
order by order_num

select DISTINCT (sm.menu_id),
from sys_menu sm left join sys_role_menu srm on sm.menu_id=srm.menu_id
where sm.parent_id=#{parentId}
and srm.role_id=#{roleId}
order by order_num
